import React, { useState, useEffect } from "react";
import styled from "styled-components";

export default function About() {
  let width;
  let height;
  const [mouseX, setX] = useState();
  const [mouseY, setY] = useState();
  useEffect(() => {
    height = window.innerHeight;
    width = window.innerWidth;
    center = {
      x: width / 2 - 400 / 2,
      y: height / 2 - 200 / 2,
      w: 400,
      h: 200,
    };
    const update = (e) => {
      setX(parseInt(e.x));
      setY(parseInt(e.y));
    };
    window.addEventListener("mousemove", update);
    window.addEventListener("touchmove", update);

    return () => {
      window.removeEventListener("mousemove", update);
      window.removeEventListener("touchmove", update);
    };
  }, [setX, setY]);
  let center;
  function lerp(start, end, amt) {
    return (1 - amt) * start + amt * end;
  }
  function map(val, max, min, x1, x2) {
    let t1 = (val - min) / (max - min);
    return (t1 - x1) / (x2 - x1);
  }
  const [redx, setRedx] = useState(0);
  const [redy, setRedy] = useState(0);

  const redd = {
    top: `${redx}px`,
    left: `${redy}px`,
  };
  function update() {
    setRedx(lerp(redx, mouseX, 0.05));
    setRedy(lerp(redy, mouseY, 0.05));
    console.log(mouseX);
  }
  setInterval(() => {
    update();
  }, 10);
  return (
    <Community>
      <Circle style={redd} />
      <p>
        <Red>STUDENT PRESS</Red> will exist as a collective which both <br />
        <Red>
          <sup>1 </sup>COMMUNICATES FOR THE STUDENTS
        </Red>{" "}
        while <br />
        building a{" "}
        <Red>
          <sup>2 </sup>SENSE OF COMMUNITY
        </Red>
      </p>
    </Community>
  );
}

const Community = styled.div`
  position: relative;
  margin-top: -100vh;
  background-color: #1e1e1e;
  display: flex;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  p {
    font-family: baskerville-urw, serif;
    font-weight: 400;
    font-style: italic;
    font-size: 3vw;
    color: white;
  }
`;
const Red = styled.span`
  font-family: urw-din, sans-serif;
  font-weight: 500;
  font-style: normal;
  color: #ff5252;
  sup {
    color: white;
    font-size: 1.2vw;
  }
`;

const Circle = styled.span`
  height: 250px;
  width: 250px;
  background-color: #bbb;
  border-radius: 50%;
  position: absolute;
  /* top: 100px;
  left: 100px; */
`;
