import React from "react";
import styled from "styled-components";
import { animate, motion } from "framer-motion";

export default function Landing() {
  const speed = 100;
  function textFn(myArray) {
    let myText = "";
    for (let i = 0; i < myArray.length; i++) {
      myText = myText + myArray[i];
    }
    return myText;
  }
  const [text1, setText1] = useState({
    name: "asd6^*",
    dName: "VOICE",
    reveal() {
      var one = this.name.split("");
      var two = this.dName.split("");
      for (let i = 0; i < one.length; i++) {
        setTimeout(function () {
          one[i] = two[i];
          const newText = textFn(one);
          setText1((t) => ({
            ...t,
            name: newText,
          }));
        }, i * speed);
      }
    },
  });
  const [text2, setText2] = useState({
    name: "?^ s(*aksjjsok*ak",
    dName: "Design Experiences",

    reveal() {
      console.log("wtf");
      var one = this.name.split("");
      var two = this.dName.split("");
      for (let i = 0; i < one.length; i++) {
        setTimeout(function () {
          one[i] = two[i];
          const newText = textFn(one);
          console.log(newText);
          setText2((t) => ({
            ...t,
            name: newText,
          }));
        }, i * speed);
      }
    },
  });

  useEffect(() => {
    setTimeout(() => {
      text1.reveal();
    }, 500);
    setTimeout(() => {
      text2.reveal();
    }, 500 + text1.name.length * speed);
  }, []);
  return (
    <Wrapper>
      <HeaderContainer>
        <Header>
          {text1} <br></br>
          <OfSpan></OfSpan> OF <br></br> <StudentSpan></StudentSpan> STUDENTS
        </Header>
      </HeaderContainer>
    </Wrapper>
  );
}

let secondaryColor = "#FF5252";
let primaryColor = "#1E1E1E";
const OfSpan = styled.span`
  padding-left: 8vw;
`;
const StudentSpan = styled.span`
  padding-left: 3.7vw;
`;
const Wrapper = styled.div`
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  color: ${primaryColor};
  background-color: ${secondaryColor};
  width: 100vw;
  height: 100vh;
`;
const HeaderContainer = styled.div`
  display: flex;
`;
const Header = styled(motion.h1)`
  font-size: 11vw;
  line-height: 0.9em;
  font-family: urw-din-condensed, sans-serif;
  font-weight: 700;
`;
