import React, { useState, useEffect } from "react";
import styled from "styled-components";

export default function About() {
  let width;
  let height;
  const [mouseX, setX] = useState();
  const [mouseY, setY] = useState();
  useEffect(() => {
    height = window.innerHeight;
    width = window.innerWidth;
    center = {
      x: width / 2 - 400 / 2,
      y: height / 2 - 200 / 2,
      w: 400,
      h: 200,
    };
    const update = (e) => {
      setX(e.x);
      setY(e.y);
    };
    window.addEventListener("mousemove", update);
    window.addEventListener("touchmove", update);
    red.centerFn();
    return () => {
      window.removeEventListener("mousemove", update);
      window.removeEventListener("touchmove", update);
    };
  }, [setX, setY]);
  let center;
  function lerp(start, end, amt) {
    return (1 - amt) * start + amt * end;
  }
  function map(val, max, min, x1, x2) {
    let t1 = (val - min) / (max - min);
    return (t1 - x1) / (x2 - x1);
  }
  let redx = 0;
  let redy = 0;
  const [red, setRed] = useState({
    x1: 0,
    x2: 0,
    y1: 0,
    y2: 0,
    x: 100,
    y: 100,
    r: 300,
    center: {},
    centerFn: function () {
      this.x1 = center.x - 80;
      this.x2 = center.x + 80;
      this.y1 = center.y - 80;
      this.y2 = center.y + 80;
    },
    values: function () {
      redx = lerp(redx, mouseX, 0.015);
      redy = lerp(redy, mouseY, 0.015);
      this.x = map(redx, 0, width, this.x1, this.x2);
      this.y = map(redy, 0, height, this.y1, this.y2);
    },
  });

  const redd = {
    top: `${red.y1}px`,
    left: `${red.x}px`,
  };

  red.values();
  return (
    <Community>
      <Circle style={redd} />
      <p>
        <Red>STUDENT PRESS</Red> will exist as a collective which both <br />
        <Red>
          <sup>1 </sup>COMMUNICATES FOR THE STUDENTS
        </Red>{" "}
        while <br />
        building a{" "}
        <Red>
          <sup>2 </sup>SENSE OF COMMUNITY
        </Red>
      </p>
    </Community>
  );
}

const Community = styled.div`
  position: relative;
  margin-top: -100vh;
  background-color: #1e1e1e;
  display: flex;
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  p {
    font-family: baskerville-urw, serif;
    font-weight: 400;
    font-style: italic;
    font-size: 3vw;
    color: white;
  }
`;
const Red = styled.span`
  font-family: urw-din, sans-serif;
  font-weight: 500;
  font-style: normal;
  color: #ff5252;
  sup {
    color: white;
    font-size: 1.2vw;
  }
`;

const Circle = styled.span`
  height: 250px;
  width: 250px;
  background-color: #bbb;
  border-radius: 50%;
  position: absolute;
  /* top: 100px;
  left: 100px; */
`;
