import React from "react";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { animate, motion } from "framer-motion";

export default function Entry() {
  const animation = {
    hidden: {
      opacity: 0,
      transition: {
        // ease: "easeInOut",
        // delayChildren: 0.5,
        staggerChildren: 0.6,
      },
    },
    show: {
      opacity: 1,
      transition: {
        // ease: "easeInOut",
        // delayChildren: 0.5,
        staggerChildren: 0.6,
      },
    },
  };

  const childAnimation = {
    show: {
      y: 0,
      transition: {
        duration: 0.8,
        ease: "easeInOut",
      },
    },
    hidden: {
      y: 200,
    },
  };
  return (
    <Wrapper>
      <TextContainer variants={animation} initial='hidden' animate='show'>
        {/* <HeadWrapper> */}
        <Header variants={childAnimation}>OCAD U</Header>
        <Header variants={childAnimation}>OCAD U</Header>
        {/* </HeadWrapper> */}
        {/* <HeadWrapper>
          <Header variants={childAnimation} initial='hidden' animate='visible'>
            STUDENT
          </Header>
        </HeadWrapper>
        <HeadWrapper>
          <Header variants={childAnimation} initial='hidden' animate='visible'>
            PRESS
          </Header>
        </HeadWrapper> */}
      </TextContainer>
    </Wrapper>
  );
}
let secondaryColor = "#FF5252";
let primaryColor = "#1E1E1E";
const TextContainer = styled.div`
  align-items: flex-start;
  display: flex;
  flex-direction: column;
`;
const Wrapper = styled(motion.div)`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin: auto;
  width: 100vw;
  height: 100vh;
  background: ${secondaryColor};
  color: white;
`;

const HeadWrapper = styled(motion.div)`
  overflow: hidden;
`;
const Header = styled(motion.div)`
  font-family: urw-din-condensed, sans-serif;
  font-weight: 500;
  font-size: 8vw;
  line-height: 0.9em;
`;
